import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Fruit from './Fruit'

class App extends Component 
{
  state = {
    fruits: []
  };


    insertIntoTable=()=>{
    let val = this.refs.textbox.value;
    if(val===''||this.countCharacter(val)>1)
    {
      alert("input is wrong please provide proper input")
    }
    else
    {
    let name =  val.split("-");
    let names = name[0];
    let price = name[1];
    var numbers = /^[0-9]+$/;
    if(price.value.matches(numbers))
    {
      alert("price should be an integer");
    }
    else
    {
    const fruits = [...this.state.fruits];
    const fruit =
    {
      name:names,
      price:price
    }
    fruits.push(fruit);
    this.setState({fruits:fruits});
    this.clearTextArea();
  }
  }
  }

  clearTextArea()
  {
    document.getElementById("textBoxId").value='';
  }

   deleteFruitHandler = (index) => {
    const fruits = [...this.state.fruits];
    fruits.splice(index, 1);
    this.setState({fruits: fruits});
  }

  countCharacter(string)
  {
    let count =0;
    for(let elements of string)
    {
      if(elements=='-')
      {
        count=count+1;
      }
    }
    return count;
  }

  handleKeyPress = (target) =>{
    if(target.charCode==13){
      let val = document.getElementById("textBoxId").value;
      console.log(val);
      let name =  val.split("-");
      let names = name[0];
      let price = name[1];
      const fruits = [...this.state.fruits];
      const fruit =
      {
        name:names,
        price:price
      }
      fruits.push(fruit);
      this.setState({fruits:fruits});
      this.clearTextArea();
    } 
  }


  render() {
    return (
      <div className="App">
        <p>React Assignment</p>

        <input ref="textbox" id="textBoxId" onKeyPress={this.handleKeyPress} type="text"/>


        <button onClick={this.insertIntoTable}>Submit</button>
        {        
             this.state.fruits.map((fruit,index) => {
            return <Fruit
            name={fruit.name} 
            price={fruit.price}
            deleteFruitHandler={() => this.deleteFruitHandler(index)}
        />
      })}
      </div>
    );
  }
}

export default App;
