import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Fruit from './Fruit'

class App extends Component 
{
  state = {
    fruits: []
  };


    insertIntoTable=()=>{
    let val = this.refs.textbox.value;
    let name =  val.split("-");
    let names = name[0];
    let price = name[1];
    const fruits = [...this.state.fruits];
    const fruit =
    {
      name:names,
      price:price
    }
    fruits.push(fruit);
    this.setState({fruits:fruits});
  }

   deleteFruitHandler = (index) => {
    const fruits = [...this.state.fruits];
    fruits.splice(index, 1);
    this.setState({fruits: fruits});
  }

  handleKeyPress(target) {
    if(target.charCode==13){
      let val = document.getElementById("h").value;
      console.log(val);
      let name =  val.split("-");
      let names = name[0];
      let price = name[1];
      const fruits = [...this.state.fruits];
      const fruit =
      {
        name:names,
        price:price
      }
      fruits.push(fruit);
      this.setState({fruits:fruits});
    } 
  }


  render() {
    return (
      <div className="App">
        <p>React Assignment</p>

        <input ref="textbox" id="h" onKeyPress={this.handleKeyPress} type="text"/>


        <button onClick={this.insertIntoTable}>Submit</button>
        {        
             this.state.fruits.map((fruit,index) => {
            return <Fruit
            name={fruit.name} 
            price={fruit.price}
            c={() => this.deleteFruitHandler(index)}
        />
      })}
      </div>
    );
  }
}

export default App;
